export default {
  data() {
    return {
      message: '',
      ruleForm: {
        email: ''
      },
      rules: {
        // telephoneNumber: [{ validator: telephoneNumber, trigger: 'blur' }],
        email: [{
          required: true,
          validator: this.email_blur(),
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    email_blur() {
      var verify = /^\w[-\w.+]*@([A-Za-z0-9][-A-Za-z0-9]+\.)+[A-Za-z]{2,14}/;
      if (!verify.test(this.email)) {
        this.message = '邮箱格式错误, 请重新输入';
      } else {
        this.message = '可以请求接口了';
      }
    }
  }
};
